    var eventDate = JSON.parse(eventDateResource);

    var separatedElements = eventDate.dataevento.split("/");
    var formattedDate = new Date(separatedElements[2], separatedElements[1] - 1, separatedElements[0]);

    var actualDate = new Date(blipActualDate);

    return actualDate.getFullYear() === formattedDate.getFullYear()
        && actualDate.getDate() === formattedDate.getDate()
        && actualDate.getMonth() === formattedDate.getMonth(); 
